import { Component } from '@angular/core';
import { Box } from './box';

@Component({
  selector: 'app-box-template',
  templateUrl: './box-template.component.html',
  styleUrls: ['./box-template.component.css']
})
export class BoxTemplateComponent {
  pageTitle = "Pokemon Fun";
  numBoxClicked = 0;
  pageTitleColor = "none";

  boxArr: Box[] = [
    {
      boxTitle: "Squirtle",
      boxBody: "Squirtle (Japanese: ゼニガメ Zenigame) is a Water-type Pokémon introduced in Generation I. It evolves into Wartortle starting at level 16, which evolves into Blastoise starting at level 36. Along with Bulbasaur and Charmander, Squirtle is one of the three starter Pokémon of Kanto available at the beginning of Pokémon Red, Green, Blue, FireRed, and LeafGreen.",
      buttonClicked: false,
      boxButton() {
        this.buttonClicked = !this.buttonClicked;
      }
    },
    {
      boxTitle: "Umbreon",
      boxBody: "Umbreon (Japanese: ブラッキー Blacky) is a Dark-type Pokémon introduced in Generation II. It evolves from Eevee when leveled up with high friendship during the night or with a Moon ShardXD in the player's Bag, except in areas with a Moss Rock or Ice Rock or while knowing a Fairy-type moveSwShLASV (and having at least two levels of affectionVI–VII). It is one of Eevee's final forms, the others being Vaporeon, Jolteon, Flareon, Espeon, Leafeon, Glaceon, and Sylveon. Umbreon is the starter Pokémon of Pokémon Colosseum alongside Espeon.",
      buttonClicked: false,
      boxButton() {
        this.buttonClicked = !this.buttonClicked;
      }
    },
    {
      boxTitle: "Charmander",
      boxBody: "Charmander (Japanese: ヒトカゲ Hitokage) is a Fire-type Pokémon introduced in Generation I. It evolves into Charmeleon starting at level 16, which evolves into Charizard starting at level 36. Along with Bulbasaur and Squirtle, Charmander is one of the three starter Pokémon of Kanto available at the beginning of Pokémon Red, Green, Blue, FireRed, and LeafGreen.",
      buttonClicked: false,
      boxButton() {
        this.buttonClicked = !this.buttonClicked;
      }
    },
    {
      boxTitle: "Bulbasaur",
      boxBody: "Bulbasaur (Japanese: フシギダネ Fushigidane) is a dual-type Grass/Poison Pokémon introduced in Generation I. It evolves into Ivysaur starting at level 16, which evolves into Venusaur starting at level 32. Along with Charmander and Squirtle, Bulbasaur is one of the three starter Pokémon of Kanto available at the beginning of Pokémon Red, Green, Blue, FireRed, and LeafGreen.",
      buttonClicked: false,
      boxButton() {
        this.buttonClicked = !this.buttonClicked;
      }
    }
  ]
}
