import { Component } from '@angular/core';
import { Box } from './box';

@Component({
  selector: 'app-box-template',
  templateUrl: './box-template.component.html',
  styleUrls: ['./box-template.component.css']
})
export class BoxTemplateComponent {
  pageTitle = "This is the title";
  Box1 : Box = {
    boxTitle : "Hello",
    boxBody : "Bye",
    boxButton() {}
  }
  Box2 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
  Box3 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
  Box4 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
}
<h1 class="pageTitle">{{pageTitle}}</h1>
<div class="Box">
    <div class="BoxTitle">{{Box1.boxTitle}}</div>
    <div class="BoxBody">{{Box1.boxBody}}</div>
</div>
<div class="Box">
    <div class="BoxTitle">{{Box2.boxTitle}}</div>
    <div class="BoxBody">{{Box2.boxBody}}</div>
</div>
<div class="Box">
    <div class="BoxTitle">{{Box3.boxTitle}}</div>
    <div class="BoxBody">{{Box3.boxBody}}</div>
</div>
<div class="Box">
    <div class="BoxTitle">{{Box4.boxTitle}}</div>
    <div class="BoxBody">{{Box4.boxBody}}</div>
</div>
