import { Component } from '@angular/core';
import { Box } from './box';

@Component({
  selector: 'app-box-template',
  templateUrl: './box-template.component.html',
  styleUrls: ['./box-template.component.css']
})
export class BoxTemplateComponent {
  pageTitle = "This is the title";
  Box1 : Box = {
    boxTitle : "Hello",
    boxBody : "Bye",
    boxButton() {}
  }
  Box2 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
  Box3 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
  Box4 : Box = {
    boxTitle : "Help",
    boxBody : "Me",
    boxButton() {}
  }
}
